descriptor.templateCss:
.sidenav {
  height: 100%;
  width: 100%; 
  display: flex;
  flex-direction: column;
  background-color: transparent; 
  overflow-x: hidden; 
  color: unset;
}

.sidenav nav {
  display: flex;
  flex-direction: column; 
  flex-grow: 1;
}

.sidenav nav a {
  padding: 16px;
  text-decoration: none;
  font-size: 16px;
  color: inherit;
  display: block;
  transition: 0.3s;
  border-bottom: 1px solid lighgrey;
}

#mySidenav a:hover {
  color: #673AB7;
  border-bottom: 1px solid #673AB7;
  font-weight: bold;
}
#mySidenav a:hover:not {
  border-bottom: 1px solid #673AB7;
  font-weight: bold;
  color: #673AB7;
}
#mySidenav a:focus{
    border-bottom: 1px solid #673AB7;
    color: #673AB7;
    font-weight: bold;
}
#mySidenav a:active{
    border-bottom: 1px solid #673AB7;
    color: #673AB7;
    font-weight: bold;
}
.sidenav .search-icon {
  margin-left: auto;
  margin-right: 16px;
  margin-top: 10px;
}

.sidenav .title {
  font-size: 14px;
  padding-left: 16px;
  color: inherit;
  /*margin-bottom: 10px; */
}

