    self.ctx.$scope.groupDashboardId = self.ctx.settings.groupDashboardId
    const groupDashboardId = self.ctx.settings.groupDashboardId
    console.log("seting",self.ctx.settings)
    function changeDashboardState(e, stateId) {
        console.log("e",e)
        console.log("stateId",stateId)
        e.preventDefault();
            // self.ctx.stateController.openState(stateId, {},false)
            self.ctx.router.navigateByUrl('/dashboards/'+ groupDashboardId + '?state=' + stateId)

        //   self.ctx.router.navigate(self.ctx.stateController.openState)
    }

    self.ctx.$scope.changeDashboardState =
