    display: flex;
    flex-direction: row;
    justify-content: space-around;
    align-items: center;
    background-color: transparent;
    width: 100%;
    height: 100%;
    border-radius: 10px;
}
nav{
    display: flex;
    flex-direction: row;
    justify-content: space-around;
    align-items: center;
    border-bottom: 1px solid rgba(64, 84, 178, .25);
    width: 100%;
}
#mySidenav a{
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
  transition: 0.3s;
  padding: 10px;
 fill: unset;
  text-decoration: none;
  font-size: 16px;
  color: unset;
  background-color: transparent;
  border-bottom: 1px solid rgba(64, 84, 178, 0);
}

#mySidenav a:hover {
  color: #673AB7;
  border-bottom: 1px solid #673AB7;
  fill: #673AB7;
}
#mySidenav a:hover:not {
  border-bottom: 1px solid #673AB7;
  color:#673AB7;
  fill: #673AB7;
}
#mySidenav a:focus{
    border-bottom: 1px solid #673AB7;
    color: #673AB7;
    fill: #673AB7;
}
#mySidenav a:active{
    border-bottom: 1px solid #673AB7;
    color: #673AB7;
    fill: #673AB7;
}

svg{
    width: 20px;
    color: unset;
    fill: unset;
}

