self.onInit = function() {
    const settings = self.ctx.settings;
    console.log("dados" ,self.ctx);
    self.ctx.$scope.links = settings.links || [];
    self.ctx.$scope.groupDashboardId = settings.groupDashboardId;

    // ðŸ§  LÃª o ID do dashboard anterior, se existir nos parÃ¢metros de estado
    const fromDashboardId = self.ctx.stateParams?.fromDashboardId || null;

    self.ctx.$scope.backToPreviousDashboard = function(e) {
        e.preventDefault();
        if (fromDashboardId) {
            self.ctx.navigateDashboard(fromDashboardId);
        } else {
            self.ctx.notify('Dashboard anterior nÃ£o disponÃ­vel', 'warn');
        }
    };

    function changeDashboardState(e, stateId) {
        e.preventDefault();
        console.log("Clicado:", stateId);
        self.ctx.stateController.openState(stateId, {}, false);
    }

    self.ctx.$scope.changeDashboardState = changeDashboardState;
};


