descriptor.templateCss:
#mySidenav{
    display: flex;
    flex-direction: row;
    justify-content: space-around;
    align-items: center;
    background-color: transparent	;
    width: 100%;
    height: 100%;
    border-radius: 10px;
}
nav{
    display: flex;
    flex-direction: row;
    justify-content: space-around;
    align-items: center;
    border-bottom: 1px solid rgba(64, 84, 178, .25);
    width: 100%;
}
#mySidenav a{
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
  transition: 0.3s;
  padding: 10px;
 
  text-decoration: none;
  font-size: 16px;
  color: unset;
  background-color: transparent;
  border-bottom: 1px solid rgba(64, 84, 178, 0);
}

#mySidenav a:hover {
  color: #673AB7;
  border-bottom: 1px solid #673AB7;
  font-weight: bold;
}
#mySidenav a:hover:not {
  border-bottom: 1px solid #673AB7;
  font-weight: bold;
  color: #673AB7;
}
#mySidenav a:focus{
    border-bottom: 1px solid #673AB7;
    color: #673AB7;
    font-weight: bold;
}
#mySidenav a:active{
    border-bottom: 1px solid #673AB7;
    color: #673AB7;
    font-weight: bold;
}

descriptor.controllerScript:
self.onInit = function() {
    const settings = self.ctx.settings;

    self.ctx.$scope.links = settings.links || [];
    self.ctx.$scope.groupDashboardId = settings.groupDashboardId;

    function changeDashboardState(e, stateId) {
        e.preventDefault();
        console.log("Clicado:", stateId);

        // ✅ Este é o método correto para trocar de estado dentro do mesmo dashboard:
        self.ctx.stateController.openState(stateId, {}, false);
    }

    self.ctx.$scope.changeDashboardState = changeDashboardState;
};

descriptor.settingsSchema:
{
  "schema": {
    "type": "object",
    "title": "Settings",
    "properties": {
      "linkCount": {
        "title": "Número de Links",
        "type": "integer",
        "minimum": 1,
        "default": 1
      },
         "groupDashboardId":{
        "type": "string",
        "title": "Dashboard Dashboard ID",
        "default": "" 
      },
      "links": {
        "type": "array",
        "title": "Configuração dos Links",
        "items": {
          "type": "object",
          "properties": {
            "content": {
              "title": "Menu String",
              "type": "string",
              "default": "Menu Entry"
            },
            "stateId": {
              "title": "State ID",
              "type": "string",
              "default": ""
            },
            "enableLink": {
              "title": "Habilitar Link",
              "type": "boolean",
              "default": false
            }
          }
        },
        "minItems": 1,
        "maxItems": 15
      }
    }
  }
}
