// Helper function to format a millisecond timestamp to YYYY-MM-DD
function formatDateToYMD(timestampMs) {
    const date = new Date(timestampMs);
    const year = date.getFullYear();
    const month = ('0' + (date.getMonth() + 1)).slice(-2); // Months are 0-indexed
    const day = ('0' + date.getDate()).slice(-2);
    return `${year}-${month}-${day}`;
}

// Helper function to determine a suitable interval based on time duration
function determineInterval(startTimeMs, endTimeMs) {
    const durationMs = endTimeMs - startTimeMs;
    const durationDays = durationMs / (1000 * 60 * 60 * 24);

    console.log('Duration days: ', durationDays);
    if (durationDays > 2) { // More than 2 days
        return '1 month';
    } else { // 2 days or less
        return '1 day';
    }
}

self.onInit = async function() {
    self.chartInstance = null;
    self.chartContainerElement = document.createElement('div');
    self.chartContainerElement.style.width = '100%';
    self.chartContainerElement.style.height = '100%';
    self.chartContainerElement.style.minHeight = '300px'; // Ensure a minimum height
    self.ctx.$container.append(self.chartContainerElement);

    try {
        const attributeService = self.ctx.$scope.$injector.get(
            self.ctx.servicesMap.get('attributeService'));

        // console.log(self.ctx.defaultSubscription.data[0]
        //     .datasource);

        const { entityId, entityType } = self.ctx.defaultSubscription.data[0]
            .datasource;

        const deviceAttributes = await attributeService.getEntityAttributes(
            { id: entityId, entityType }, // Use the resolved entity object
            'SERVER_SCOPE',
            ['centralId', 'slaveId', 'channel']
        ).toPromise();

        const centralIdAttr = deviceAttributes.find(attribute => attribute.key === 'centralId');
        const slaveIdAttr = deviceAttributes.find(attribute => attribute.key === 'slaveId');
        const channelAttr = deviceAttributes.find(attribute => attribute.key === 'channel');

        const centralId = centralIdAttr ? centralIdAttr.value : null;
        const slaveIdStr = slaveIdAttr ? slaveIdAttr.value : null;
        const channelStr = channelAttr ? channelAttr.value : null;
        const slaveId = slaveIdStr ? parseInt(slaveIdStr) : null;
        // Channel is optional, defaults to 0 if not provided
        const channel = channelStr ? parseInt(channelStr) : 1;

        if (!centralId || slaveId === null || isNaN(slaveId)) {
            console.error(`Missing or invalid water device attributes: centralId='${centralId}', slaveId='${slaveIdStr}'. Cannot initialize water chart.`);
            self.chartContainerElement.innerHTML = `<div style="padding: 20px; text-align: center; color: red;">Error: Missing or invalid water device attributes (centralId or slaveId). Check device server attributes.</div>`;
            return;
        }

        // Log channel default if not explicitly set
        if (!channelAttr) {
            console.log(`Channel attribute not found, using default channel: 0`);
        }

        self.ctx.$scope.centralId = centralId;
        self.ctx.$scope.slaveId = slaveId;
        self.ctx.$scope.channel = channel;

        initChart(slaveId, centralId, channel);
    } catch (e) {
        console.error('Error fetching water device attributes or initializing chart:', e);
        self.chartContainerElement.innerHTML = `<div style="padding: 20px; text-align: center; color: red;">Error initializing water chart: ${e.message || 'Unknown error'}</div>`;
    }
};

function initChart(slaveId, centralId, channel) {
    // Destroy previous instance if it exists
    if (self.chartInstance && typeof self.chartInstance.destroy === 'function') {
        self.chartInstance.destroy();
        self.chartInstance = null;
    }
    // Ensure container is clean (SDK's destroy should handle iframe, but good practice)
    if (self.chartContainerElement) {
        self.chartContainerElement.innerHTML = '';
    }

    let renderWaterChart;
    if (window.EnergyChartSDK && typeof window.EnergyChartSDK.renderWaterChart === 'function') {
        renderWaterChart = window.EnergyChartSDK.renderWaterChart;
    } else {
        console.error('WaterChartSDK not loaded! Make sure EnergyChartSDK contains renderWaterChart function.');
        if (self.chartContainerElement) {
            self.chartContainerElement.innerHTML = '<div style="padding: 20px; text-align: center; color: red;">WaterChartSDK not loaded. Check widget configuration and browser console.</div>';
        }
        return;
    }

    const settings = self.ctx.settings || {};
    const timeWindow = self.ctx.timeWindow;
    // console.log('time window: ', timeWindow)

    if (!timeWindow || typeof timeWindow.minTime === 'undefined' || typeof timeWindow.maxTime === 'undefined') {
        console.error('Time window is not available or has incorrect format (missing minTime/maxTime).');
        if (self.chartContainerElement) {
             self.chartContainerElement.innerHTML = '<div style="padding: 20px; text-align: center; color: orange;">Time window not available for water chart.</div>';
        }
        return;
    }

    const gatewayId = centralId;
    const startDate = formatDateToYMD(timeWindow.minTime); // Use minTime
    const endDate = formatDateToYMD(timeWindow.maxTime);   // Use maxTime
    const interval = determineInterval(timeWindow.minTime, timeWindow.maxTime);
    const theme = settings.theme || 'light';
    const apiBaseUrl = settings.apiBaseUrl || 'https://ingestion.myio-bas.com';
    const timezone = timeWindow.timezone || settings.timezone || 'America/Sao_Paulo'; // Prioritize timewindow timezone

    console.log(`Initializing water chart with: gatewayId=${gatewayId}, slaveId=${slaveId}, channel=${channel}, startDate=${startDate}, endDate=${endDate}, interval=${interval}, theme=${theme}, apiBaseUrl=${apiBaseUrl}, timezone=${timezone}`);

    self.chartInstance = renderWaterChart(self.chartContainerElement, {
        gatewayId: gatewayId,
        slaveId: slaveId,
        channel: channel,
        startDate: startDate,
        endDate: endDate,
        interval: interval,
        theme: theme,
        timezone: timezone,
        iframeBaseUrl: settings.iframeBaseUrl || 'https://graphs.ingestion.myio-bas.com', // Make iframeBaseUrl configurable
        apiBaseUrl: apiBaseUrl,
        chartPath: settings.chartPath || '/embed/water-bar' // Make chartPath configurable for water
    });

    // Attach event listeners if SDK supports it
    if (self.chartInstance && typeof self.chartInstance.on === 'function') {
        self.chartInstance.on('drilldown', (data) => {
            console.log('Water SDK Drilldown Event:', data);
            // Example: Emit custom event for ThingsBoard dashboard actions
            // self.ctx.actionsApi.handleWidgetAction({ actionIdentifier: 'customWaterDrilldown', dataContext: data });
        });
        self.chartInstance.on('error', (errorData) => {
            console.error('Water SDK Error Event:', errorData);
             if (self.chartContainerElement) {
                self.chartContainerElement.innerHTML = `<div style="padding: 20px; text-align: center; color: red;">Water Chart Error: ${errorData.message || 'Unknown error'}</div>`;
            }
        });
    } else if(self.chartInstance) {
        console.warn("WaterChartSDK instance does not have an 'on' method for event listeners.");
    }
}

self.onTimewindowChanged = function(newTimeWindow) {
    if (self.chartInstance && typeof self.chartInstance.changePeriod === 'function') {
        const startDate = formatDateToYMD(newTimeWindow.minTime); // Use minTime
        const endDate = formatDateToYMD(newTimeWindow.maxTime);   // Use maxTime
        const interval = determineInterval(newTimeWindow.minTime, newTimeWindow.maxTime);
        const newTimezone = newTimeWindow.timezone || self.ctx.settings.timezone || 'America/Sao_Paulo';

        console.log(`Water chart time window changed. Updating chart to: startDate=${startDate}, endDate=${endDate}, interval=${interval}, timezone=${newTimezone}`);
        self.chartInstance.changePeriod(startDate, endDate, interval);
        // If timezone changed and is important for changePeriod, consider:
        // self.chartInstance.updateOptions({ timezone: newTimezone }); 
        // (This might cause a double update or re-fetch, depending on SDK)
    } else if (!self.chartInstance) {
        // If chart wasn't initialized for some reason (e.g., SDK loaded late, or initial error)
        console.log('Water chart time window changed, but chart not initialized. Attempting to initialize.');
        initChart(self.ctx.$scope.slaveId, self.ctx.$scope.centralId, self.ctx.$scope.channel);
    } else {
        console.warn("Water chartInstance.changePeriod is not a function. Re-initializing for time window change.");
        initChart(self.ctx.$scope.slaveId, self.ctx.$scope.centralId, self.ctx.$scope.channel); // Fallback to re-init if changePeriod is not available
    }
};

self.onSettingsChanged = function(previousSettings, newSettings) {
    // Fetch current centralId, slaveId and channel from scope, which should have been set by onInit
    const currentCentralId = self.ctx.$scope.centralId;
    const currentSlaveId = self.ctx.$scope.slaveId;
    const currentChannel = self.ctx.$scope.channel;

    // Check if widget settings relevant to SDK config changed
    const oldTheme = previousSettings.theme || 'light';
    const newTheme = newSettings.theme || 'light';
    const oldTimezone = previousSettings.timezone || 'America/Sao_Paulo';
    const newTimezone = newSettings.timezone || 'America/Sao_Paulo';
    const oldApiBaseUrl = previousSettings.apiBaseUrl || 'https://ingestion.myio-bas.com';
    const newApiBaseUrl = newSettings.apiBaseUrl || 'https://ingestion.myio-bas.com';
    const oldIframeBaseUrl = previousSettings.iframeBaseUrl || 'https://graphs.ingestion.myio-bas.com';
    const newIframeBaseUrl = newSettings.iframeBaseUrl || 'https://graphs.ingestion.myio-bas.com';
    const oldChartPath = previousSettings.chartPath || '/embed/water-bar';
    const newChartPath = newSettings.chartPath || '/embed/water-bar';

    let reInitNeeded = false;
    let optionsToUpdate = {};

    if (newApiBaseUrl !== oldApiBaseUrl || 
        newIframeBaseUrl !== oldIframeBaseUrl || 
        newChartPath !== oldChartPath) {
        reInitNeeded = true;
        console.log('Water chart API/Iframe/ChartPath settings changed. Re-initializing chart.');
    }

    if (newTheme !== oldTheme) {
        optionsToUpdate.theme = newTheme;
    }
    if (newTimezone !== oldTimezone) {
        optionsToUpdate.timezone = newTimezone;
    }

    if (reInitNeeded) {
        // Pass the currently known centralId, slaveId and channel to initChart
        // These IDs are not expected to change via widget settings, but via device attributes
        initChart(currentSlaveId, currentCentralId, currentChannel);
    } else if (Object.keys(optionsToUpdate).length > 0) {
        if (self.chartInstance && typeof self.chartInstance.updateOptions === 'function') {
            console.log('Water chart theme or timezone widget setting changed. Updating chart options:', optionsToUpdate);
            self.chartInstance.updateOptions(optionsToUpdate);
        } else if (self.chartInstance) {
            console.warn("Water chartInstance.updateOptions is not available. Re-initializing for settings change.");
            initChart(currentSlaveId, currentCentralId, currentChannel);
        }
    }
};

// Standard ThingsBoard widget lifecycle methods
self.onDataUpdated = function() {
    // Called when historical data is updated.
    // If your water chart needs to react to external data source changes (not just time window or settings),
    // you might refresh or update it here.
};

self.onLatestDataUpdated = function() {
    // Called when real-time data/latest telemetry is updated.
};

self.onMobileModeChanged = function() {
    // Handle mobile mode changes if necessary
};

self.onHasTimewindowChanged = function() {
    // Called when the widget's "has timewindow" state changes
};

self.onResize = function() {
    // The chart iframe should be responsive.
    // If specific resize handling is needed for the SDK instance:
    // if (self.chartInstance && typeof self.chartInstance.resize === 'function') {
    //     self.chartInstance.resize();
    // }
};

self.onDestroy = function() {
    if (self.chartInstance && typeof self.chartInstance.destroy === 'function') {
        self.chartInstance.destroy();
        self.chartInstance = null;
    }
    if (self.chartContainerElement && self.chartContainerElement.parentNode) {
        // Clean up the container DIV we created
        self.chartContainerElement.parentNode.removeChild(self.chartContainerElement);
        self.chartContainerElement = null;
    }
};

