descriptor.templateCss:
.sidenav {
  height: 100%;
  width: 100%; 
  display: flex;
  flex-direction: column;
  background-color: transparent; 
  overflow-x: hidden; 
  color: white;
}

.sidenav nav {
  display: flex;
  flex-direction: column; 
  flex-grow: 1;
}

.sidenav nav a {
  padding: 16px;
  text-decoration: none;
  font-size: 16px;
  color: inherit;
  display: block;
  transition: 0.3s;
  border-bottom: 1px solid white;
}

#mySidenav a:hover {
  color: black;
  border-bottom: 1px solid black;
}
#mySidenav a:hover:not {
  border-bottom: 1px solid black;
  color: black;
}
#mySidenav a:focus{
    border-bottom: 1px solid black;
    color: black;
}
#mySidenav a:active{
    border-bottom: 1px solid black;
    color: black;
}

.sidenav .search-icon {
  margin-left: auto;
  margin-right: 16px;
  margin-top: 10px;
}

.sidenav .title {
  font-size: 14px;
  padding-left: 16px;
  color: inherit;
  /*margin-bottom: 10px; */
}

descriptor.controllerScript:
self.onInit = function() {
    self.ctx.$scope.links = self.ctx.settings.links || [];
    // console.log(self.ctx.router)

    function changeDashboardState(e, link) {
        e.preventDefault();

        self.ctx.router.navigateByUrl('/dashboards/' + link);
    }

    self.ctx.$scope.changeDashboardState = changeDashboardState;
}


