descriptor.settingsSchema:
{
  "schema": {
    "type": "object",
    "title": "Settings",
    "properties": {
      "linkCount": {
        "title": "Número de Links",
        "type": "integer",
        "minimum": 1,
        "default": 1
      },
      "links": {
        "type": "array",
        "title": "Configuração dos Links",
        "items": {
          "type": "object",
          "properties": {
            "content": {
              "title": "Menu String",
              "type": "string",
              "default": "Menu Entry"
            },
            "link": {
              "title": "DashboardID",
              "type": "string",
              "default": ""
            },
            "enableLink": {
              "title": "Habilitar Link",
              "type": "boolean",
              "default": false
            }
          }
        },
        "minItems": 1,
        "maxItems": 30
      }
    }
  }
}
