descriptor.templateCss:
.widget-container {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  border: 2px solid #ddd;
  border-radius: 16px;
  padding: 20px;
  background-color: #fff;
  box-shadow: 0 6px 18px rgba(0, 0, 0, 0.1);
  height: 100%;
  width: 100%;
  box-sizing: border-box;
  text-align: center;
}

.widget-img {
  height: 100px;
  max-width: 100%;
  transition: opacity 0.3s ease-in-out;
}

.widget-blink {
  animation: blink 1.2s infinite;
}

@keyframes blink {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.3; }
}

.widget-text {
  margin-top: 12px;
  font-size: 1.2em;
  font-weight: 600;
  color: #333;
}

descriptor.controllerScript:
self.onInit = function () {
  const ctx = self.ctx;
  const settings = ctx.settings || {};
  const dataKeySettings = ctx.data?.[0]?.dataKey?.settings || {};

  ctx.stateText = dataKeySettings.noDataText || 'Sem dados';
  ctx.showOn = null;
  ctx.initialized = false;

  const threshold = settings.threshold ?? 100;

  self.onDataUpdated = function () {
    try {
      const data = ctx.data?.[0]?.data;
      const value = data && data.length ? data[data.length - 1][1] : null;

      if (value == null || isNaN(value)) {
        ctx.stateText = dataKeySettings.noDataText || 'Sem dados';
        ctx.showOn = false;
        ctx.initialized = true;
        return;
      }

      const ativo = value >= threshold;
      ctx.stateText = ativo ? 'Ligado' : 'Desligado';
      ctx.showOn = ativo;
      ctx.initialized = true;
    } catch (e) {
      console.error("Erro ao processar dados:", e);
      ctx.stateText = 'Erro';
      ctx.showOn = false;
      ctx.initialized = true;
    }
  };
};

self.onResize = function () {};
self.onDestroy = function () {};

