self.onInit = function () {
  const ctx = self.ctx;
  ctx.entityMap = {};
  ctx.$container.empty();

  const $main = $('<div class="status-container"></div>');
  ctx.$container.append($main);
  
  const cards = ctx.data.filter(ds => ds.dataKey?.name === 'deviceKind')
    .sort((a, b) => (a.datasource?.entityLabel || '').localeCompare(b.datasource?.entityLabel || '', 'pt-BR'));
    
  cards.forEach((ds, index) => {
    console.log("LOG Widget STORE onInit forEach >>> " , ds);

    const label = ds.datasource?.entityLabel;
    const entityId = ds.datasource?.entityId;
    const entityType = ds.datasource?.entityType;
    const safeId = `card-${label.replace(/\s+/g, '_')}`;
    
    let imgOffLineToShow;
      
    if (label.toLowerCase().includes("rel")) {
      imgOffLineToShow = relogioOffline;
    } else if (label.toLowerCase().includes("bomba") || label.toLowerCase().includes("chiller")) {
      imgOffLineToShow = bombaOffline;
    } else if (label.toLowerCase().includes("subesta")) {
      imgOffLineToShow = subestacaoOffline;
    } else {
      imgOffLineToShow = storeOffline;
    }

    const $card = $(`
      <div class="device-card-centered clickable"
           id="${safeId}"
           data-state="default"
           data-entity-id="${entityId}"
           data-entity-type="${entityType}">
        <div class="device-title">⚡ ${label}</div>
        <img class="device-image" src="${imgOffLineToShow}" />
        <div class="device-data-row">
          <div class="consumption">⚡ <span>-- kW</span></div>
        </div>
      </div>
    `);

    const backColor = ds.dataKey?.settings?.backGroundColor;
    if (backColor) $card.css('background-color', backColor);

    ctx.entityMap[label] = {
      el: $card,
      label,
      consumption: null
    };

    $main.append($card);
  });
  
  const $cardAreaComum = $(`
      <div class="device-card-centered clickable"
           id="card-area-comum"
           data-state="default"
           data-entity-id="card-area-comum-id"
           data-entity-type="card-area-comum-type">
        <div class="device-title">⚡ Área Comum</div>
        <img class="device-image" src="${areaComumCampinasShopping}" />
        <div class="device-data-row">
          <div class="consumption">⚡ <span>-- kW</span></div>
        </div>
      </div>
  `);

  ctx.entityMap["Área Comum"] = {
    el: $cardAreaComum,
    label: "Área Comum",
    consumption: 0
  };

  $main.append($cardAreaComum);    

  ctx.$container.on('click', '.device-card-centered', function (e) {
    e.preventDefault();
    const entityId = $(this).data('entity-id');
    const entityType = $(this).data('entity-type');
    ctx.stateController.openState('default', {
      entityId: { id: entityId, entityType }
    }, false);
  });
};

self.onDataUpdated = function () {
  const ctx = self.ctx;
  
  let consumptionIn = 0;
  let consumptionOut = 0;
  let consumptionAreaComum = 0;    
  
  ctx.data.forEach(ds => {
    const label = ds.datasource?.entityLabel;
    const key = ds.dataKey?.name;
    const val = ds.data?.[ds.data.length - 1]?.[1];
    const isValid = typeof val === 'number' && !isNaN(val);
    
    if (key === 'consumption') {
      if (ctx.entityMap[label]) {
        const entry = ctx.entityMap[label];
        const isOn = isValid && val > 0;
      
        let img;
      
        if (label.toLowerCase().includes("rel")) {
          consumptionIn += val;
          img = !isValid ? storeOffline : (isOn ? relogioOn : relogioOff);
        } else if (label.toLowerCase().includes("subesta")) {
          consumptionIn += val;
          img = !isValid ? storeOffline : (isOn ? subestacaoOn : subestacaoOff);
