descriptor.controllerScript:
function fetchData(){
     const subscriptionData = self.ctx.data[0].data;
     var data = !subscriptionData.length || !subscriptionData[0][1]  ? null : subscriptionData[0][1] 

    self.ctx.$scope.temperature = data ? data.toFixed(2) : '-';
    var TituloWidget = self.ctx.settings.TituloWidget;
    self.ctx.$scope.TituloWidget = TituloWidget
}

self.onInit = function() {
    self.ctx.detectChanges();
}

self.onDataUpdated = function() {
    fetchData()
    self.ctx.detectChanges();
}

self.onLatestDataUpdated = function() {
    fetchData
    self.ctx.detectChanges();
}

