descriptor.templateCss:
.status-container {
  display: flex;
  flex-direction: column;
  padding: 6px;
  height: 100%;              /* garante que ocupe o espa√ßo total do widget */
  overflow-y: auto;          /* ativa a rolagem vertical quando necess√°rio */
  overflow-x: hidden;
  box-sizing: border-box;
}

.device-card-centered {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  background: #fff;
  border-radius: 12px;
  padding: 6px 6px;
  box-shadow: 0px 4px 10px rgba(0,0,0,0.06), 0px -2px 6px rgba(0,0,0,0.04);
  text-align: center;
  width: 99%;
  max-width: 100%;
  margin: 0 2px 2px 2px;
  margin-bottom: 12px;
  cursor: pointer;
  transition: box-shadow 0.2s ease, transform 0.1s ease;
}

.device-card-centered:hover {
  box-shadow: 0px 8px 24px rgba(0,0,0,0.12), 0px -4px 16px rgba(0,0,0,0.08);
  transform: scale(1.1);
}

.device-title {
  font-weight: bold;
  font-size: 0.9rem;
  margin-bottom: 6px;
  color: #333;
}

.device-image {
  width: 90px;
  height: auto;
  margin-bottom: 6px;
  transition: transform 0.2s ease;
}

.blink {
  animation: blink 1.2s infinite;
}

.device-data-row {
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 100%;
  font-size: 0.95rem;
  color: #444;
  padding: 0 8px;
  box-sizing: border-box;
}

.temperature span {
  color: #007bff;
  font-weight: 600;
}

.consumption span {
  color: #28a745;
  font-weight: 600;
}

@keyframes blink {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.3; }
}

descriptor.controllerScript:
const offlineImg = '/api/images/public/XVSlrbdXz5jAFfYNo4ymvu3jh76Iw6Ag';
const offImg = '/api/images/public/V3nAuG6sBlMJAAOeiXWhctFKZzBuo6IL';
const fanImg = '/api/images/public/nAqgFLTCDHSyrCaboKq6R31Q45xI4NNT';
const onImg = '/api/images/public/6ziChYbLxcZuCismHWEBvCWNj6LLUet0';

const style = document.createElement('style');
style.innerHTML = `/* cole o CSS acima aqui */`;
document.head.appendChild(style);

self.onInit = function () {
  const ctx = self.ctx;
  const settings = ctx.settings || {};
  const threshold = settings.threshold ?? 100;

  ctx.tempDevices = {};
  ctx.$container.empty();
  const $main = $('<div class="status-container"></div>');
  ctx.$container.append($main);

    ctx.data.forEach((ds, index) => {
        if (ctx.data[index].dataKey.name === 'consumption') {
            const entityLabel = ctx.datasources?.[index]?.entityLabel || `Dispositivo SEM LABEL ${index + 1}`;
            const deviceId = `device-${index}`;
            const entityId = ctx.datasources?.[index]?.entityId;
            const entityType = ctx.datasources?.[index]?.entityType;
            const deviceData = ctx.data?.[index]?.data?.[0];
            const cons = deviceData && deviceData.length > 1 ? deviceData[1].toFixed(2) : null;
    
            const $device = $(`
              <div class="device-card-centered clickable" id="${deviceId}"
                   data-state="${settings.energia}"
                   data-entity-id="${entityId}"
                   data-entity-type="${entityType}">
                <div class="device-title">${entityLabel}</div>
                <img class="device-image" src="${offlineImg}" />
                <div class="device-data-row">
                  <div class="temperature">üå°Ô∏è <span>--¬∞C</span></div>
                  <div class="consumption">‚ö° <span>${cons !== null ? (cons / 1000).toFixed(2) : '--'} kW</span></div>
                </div>
              </div>
            `);
    
          $main.append($device);
        }
    });

    ctx.$container.on('click', '.device-card-centered', function (e) {
      e.preventDefault();
      const targetState = $(this).data('state');
      const entityId = $(this).data('entity-id');
      const entityType = $(this).data('entity-type');
      ctx.stateController.openState(targetState, {
        entityId: { id: entityId, entityType }
      }, false);
    });
};
    

self.onDataUpdated = function () {
  const ctx = self.ctx;

  ctx.data.forEach((ds, index) => {
    try {
      const key = ds.dataKey?.name;
      const label = ds.datasource?.entityLabel || '';
      const deviceId = `device-${index}`;
      const $device = $(`#${deviceId}`, ctx.$container);

      if (!$device.length) return;
      
      console.log("forEach - ds >>> ", ds);

      if (key === 'temperature') {
        const value = ds.data?.[0]?.[1];
        const temp = (typeof value === 'number' && !isNaN(value)) ? value.toFixed(2) : '--';
        ctx.tempDevices[label] = temp;
        $device.find('.temperature span').text(`${temp}¬∞C`);
      }

      if (key === 'consumption') {
        const val = ds.data?.[ds.data.length - 1]?.[1];
        const isOn = val > 0;
        const isFan = val <= ctx.settings.threshold;
        const $img = $device.find('.device-image');
        const $cons = $device.find('.consumption span');
        const $temp = $device.find('.temperature span');

        const img = isOn ? (isFan ? fanImg : onImg) : offImg;
        const cor = isOn ? (isFan ? "#b499eb" : "#45b2cc") : "#d6dcdd";
        const consumoFormatado = val ? (val / 1000).toFixed(2) + ' kW' : '-- kW';
        const tempValue = ctx.tempDevices[label] || '--';

        $img.attr('src', img).toggleClass('blink', isOn);
        $cons.text(consumoFormatado).css('color', cor);
        $temp.text(`${tempValue}¬∞C`);
      }
    } catch (e) {
      console.error('Erro ao atualizar dispositivo:', e);
    }
  });
};

