descriptor.templateCss:
.status-device-premium {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 14px 20px;
  border-radius: 16px;
  background-color: #fff;
  box-shadow: 0 6px 16px rgba(0, 0, 0, 0.06);
  transition: box-shadow 0.3s ease;
  gap: 16px;
  min-height: 120px;
  flex-wrap: wrap;
}

.status-device-premium:hover {
  box-shadow: 0 8px 20px rgba(0, 0, 0, 0.12);
}

.left-section {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  gap: 6px;
}

.device-name {
  font-size: 1rem;
  font-weight: 600;
  color: #333;
}

.status-img {
  height: 64px;
  transition: transform 0.2s ease;
}

.status-img.blink {
  animation: blink 1.2s infinite;
}

.status-label {
  font-size: 0.9rem;
  color: #666;
}

.right-section {
  display: flex;
  flex-direction: column;
  align-items: flex-end;
  gap: 10px;
}

.temperature-block,
.consumption-block {
  font-size: 1.1rem;
  font-weight: 600;
  color: #333;
}

.temperature-value {
  color: #0072ff;
}

.consumption-value {
  color: #28a745;
}

.clickable {
  cursor: pointer;
  transition: background 0.2s ease;
}

.clickable:hover {
  background-color: rgba(0, 0, 0, 0.04);
  border-radius: 6px;
  padding: 2px 6px;
}

@keyframes blink {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.4; }
}

