  const ctx = self.ctx;
  const settings = ctx.settings || {};

  const threshold = settings.threshold ?? 100;
  const onImg = settings.onImageUrl || '/api/images/public/6ziChYbLxcZuCismHWEBvCWNj6LLUet0';
  const fanImg = settings.fanImageUrl || '/api/images/public/nAqgFLTCDHSyrCaboKq6R31Q45xI4NNT';
  const offImg = settings.offImageUrl || '/api/images/public/V3nAuG6sBlMJAAOeiXWhctFKZzBuo6IL';
  const offlineImg = '/api/images/public/XVSlrbdXz5jAFfYNo4ymvu3jh76Iw6Ag';

  // Inject CSS
  const style = document.createElement('style');
