        let deviceData =ctx.data?.[index]?.data?.[0]
        let consuption = null;
        if (deviceData && deviceData.length > 1) {
            consuption = deviceData[1].toFixed(2);
            
        } else {
          console.log("deviceData n√£o tem √≠ndice 1");
        }
        console.log("data", entityType);
const $device = $(`
  <div class="status-device-premium" id="${deviceId}">
    <div class="left-section">
      <div class="device-name">üí° ${entityLabel}</div>
      <img class="status-img" src="${offlineImg}" alt="Estado" />
      <div class="status-label">Carregando...</div>
    </div>
    <div class="right-section">
      <div class="temperature-block">
        üå°Ô∏è <span class="temperature-value">${tempDevices[entityLabel.includes("Online") ? "Online" : "Comum"] ?? '-'}</span>¬∞C
      </div>
      <div class="consumption-block clickable"
           data-state="${stateEnergy}" 
           data-entity-id="${entityId}" 
           data-entity-type="${entityType}">
        ‚ö° <span class="consumption-value">${consuption !== null ? (consuption / 1000).toFixed(2) : '-'} kW</span>
      </div>
    </div>
  </div>
`);

    
        $main.append($device);
    }
    ctx.$container.on('click', '.status', function(e) {
      e.preventDefault();
     
      const targetState = $(this).data('state') || 'air_detail';
      ctx.stateController.openState(targetState, {}, false);
    });
    
    
    
ctx.$container.on('click', '.energy', function(e) {
  e.preventDefault();

  const targetState = $(this).data('state');
  const entityId = $(this).data('entity-id'); // string
  const entityType = $(this).data('entity-type'); // string
    console.log(entityType)
  ctx.stateController.openState(targetState, {
    entityId: {
      id: entityId,
      entityType: entityType  // j√° vem do HTML corrigido
    }
  }, false);
});



  });

  self.onDataUpdated = function () {
const tempDevices = {}; // Objeto, n√£o array

ctx.data.forEach((ds, index) => {
    try {
        const dataKeyName = ctx.data[index]?.dataKey?.name;
        if (dataKeyName === "temperature") {
            const dataEntry = ctx.data[index]?.data?.[0];
            const valor = dataEntry ? dataEntry[1] : null;

            // Se valor for null ou n√£o for n√∫mero, atribui null
            const valorFormatado = (typeof valor === 'number' && !isNaN(valor)) ? valor.toFixed(2) : null;

            const entityLabel = ctx.data[index]?.datasource?.entityLabel || "";

            if (entityLabel.includes("Online") || entityLabel.includes("CD")) {
                tempDevices["Online"] = valorFormatado;
            } else {
                tempDevices["Comum"] = valorFormatado;
            }
        }
    } catch (e) {
        // Em caso de erro, define valor null para ambas as chaves, s√≥ para garantir
        tempDevices["Online"] = null;
        tempDevices["Comum"] = null;
        console.error("Erro ao processar temperatura:", e);
    }
});



    console.log("TEMP",tempDevices)
    try {
      ctx.data.forEach((ds, index) => {
          
        const data = ds.data;
        const deviceId = `device-${index}`;
        const $device = $(`#${deviceId}`, ctx.$container);
        const $img = $device.find('img');
        const $label = $device.find('.status-label');
        const $vertical = $device.find('.vertical');
        const $status = $device.find('.status');
        const $energyIcon = $device.find('.energy-icon');
        const $energySpam = $device.find('.energy');
        const dataKeySettings = ds.dataKey?.settings || {};
        const $viewDetails = $device.find('.view-details');
        const value = data && data.length ? data[data.length - 1][1] : null;

        if (value == null || isNaN(value)) {
          $img.attr('src', offlineImg).removeClass('blink');
          $label.text(dataKeySettings.noDataText || 'Sem dados');
          $energySpam.css('color','#d86262');
          return;
        }
    
        const isOn = value > 0;
        const isFanMode = value <= threshold;
        const imgToShow = isOn ? (isFanMode ? fanImg : onImg) : offImg;
        console.log("imgToShow",imgToShow)
        const colorText = isOn ? (isFanMode ? "#b499eb" : "#45b2cc") : "#d6dcdd";
        $energyIcon.toggleClass('blink', isOn);
        $energySpam.css('color',colorText);
        $img.attr('src', imgToShow);
        $img.toggleClass('blink', isOn);
        $label.text(ctx.data[index].datasource.entityLabel.includes("Online")?tempDevices["Online"] :tempDevices["Comum"]);
        $viewDetails.text(ctx.data[index].datasource.entityLabel.includes("Online")?tempDevices["Online"] :tempDevices["Comum"]);
        $status.css({
          'cursor': 'pointer',
          'user-select': 'none',
          'background-color': '',
        });
        $energySpam.css({
          'cursor': 'pointer',
          'user-select': 'none',
          'background-color': '',
          'margin-left': 'auto',
          'margin-right': '10%',
           'float': 'right'

        });

        $device.css({
          'display': 'flex',
          'justify-content': 'space-between', // separa os elementos
          'align-items': 'center'
        });
      });
        
    } catch (err) {
      console.error('Erro ao atualizar estados m√∫ltiplos:', err);
    }
  };
};

