descriptor.templateCss:
.status-container {
  display: flex;
  flex-direction: column;
  padding: 6px;
  height: 100%;
  overflow-y: auto;
  overflow-x: hidden;
  box-sizing: border-box;
}

.device-card-centered {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  border-radius: 12px;
  padding: 6px;
  box-shadow: 0px 10px 10px rgba(0,0,0,0.06), 0px -6px 6px rgba(0,0,0,0.04);
  text-align: center;
  width: 99%;
  margin: 0 2px 12px 2px;
  cursor: pointer;
  transition: box-shadow 0.2s ease, transform 0.1s ease;
}

.device-card-centered:hover {
  box-shadow: 0px 8px 24px rgba(0,0,0,0.12), 0px -4px 16px rgba(0,0,0,0.08);
  transform: scale(1.1);
}

.device-title {
  font-weight: bold;
  font-size: 0.9rem;
  margin-bottom: 6px;
  color: #333;
}

.device-image {
  width: 90px;
  height: auto;
  margin-bottom: 6px;
  transition: transform 0.2s ease;
}

.blink {
  animation: blink 1.2s infinite;
}

.device-data-row {
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
  font-size: 0.95rem;
  color: #444;
  padding: 0 8px;
  box-sizing: border-box;
}

.consumption span {
  color: #28a745;
  font-weight: 600;
}

.back-button {
  margin: 10px;
  padding: 4px 10px;
  background: #f0f0f0;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  font-weight: bold;
}
.back-button:hover {
  background: #ddd;
}

@keyframes blink {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.3; }
}

