const storeOffline = '/api/images/public/FElpCTxLRwHYgAo3Jv7j0zKHwACsMjX0';
const storeOn      = '/api/images/public/f9Ce4meybsdaAhAkUlAfy5ei3I4kcN4k';
const storeOff     = '/api/images/public/sdTe2CPTbLPkbEXBHwaxjSAGVbp4wtIa';

const bombaOffline = '/api/images/public/XutAQB6zI47vEoy7RWO2nk0o4pGmx7Cd';
const bombaOn      = '/api/images/public/Rge8Q3t0CP5PW8XyTn9bBK9aVP6uzSTT';
const bombaOff     = '/api/images/public/8Ezn8qVBJ3jXD0iDfnEAZ0MZhAP1b5Ts';

const relogioOffline = '/api/images/public/NyAMC4UpTt366BIgLgCmhWLCxe68oTvv';
const relogioOn      = '/api/images/public/ljHZostWg0G5AfKiyM8oZixWRIIGRASB';
const relogioOff     = '/api/images/public/rYrcTQlf90m7zH9ZIbldz6KIZ7jdb5DU';

const subestacaoOffline = '/api/images/public/MjIoXIN1enEijyHsUjjheItSQCpLl36U';
const subestacaoOn      = '/api/images/public/TQHPFqiejMW6lOSVsb8Pi85WtC0QKOLU';
const subestacaoOff     = '/api/images/public/HnlvjodeBRFBc90xVglYI9mIpF6UgUmi';

const style = document.createElement('style');
style.innerHTML = `/* CSS acima aqui */`;
document.head.appendChild(style);

self.onInit = function () {
  const ctx = self.ctx;
  ctx.entityMap = {};
  ctx.groups = {
    'Administração': [],
    'Bomba / Chiller': [],
    'Relógio': [],
    'Entrada Subestação': []
  };

  ctx.$container.empty();

  const allCards = ctx.data.filter(ds => ds.dataKey?.name === 'deviceKind')
    .sort((a, b) => (a.datasource?.entityLabel || '').localeCompare(b.datasource?.entityLabel || '', 'pt-BR'));

  allCards.forEach(ds => {
    const label = ds.datasource?.entityLabel;
    const entityId = ds.datasource?.entityId;
    const entityType = ds.datasource?.entityType;
    const safeId = `card-${label.replace(/\s+/g, '_')}`;

    let imgOffLineToShow;
    let group = 'Administração';

    if (label.toLowerCase().includes("rel")) {
      imgOffLineToShow = relogioOffline;
      group = 'Relógio';
    } else if (label.toLowerCase().includes("bomba") || label.toLowerCase().includes("chiller")) {
      imgOffLineToShow = bombaOffline;
      group = 'Bomba / Chiller';
    } else if (label.toLowerCase().includes("subesta")) {
      imgOffLineToShow = subestacaoOffline;
      group = 'Entrada Subestação';
    } else {
      imgOffLineToShow = storeOffline;
    }

    const $card = $(`
      <div class="device-card-centered clickable"
           id="${safeId}"
           data-state="default"
           data-entity-id="${entityId}"
           data-entity-type="${entityType}">
        <div class="device-title">⚡ ${label}</div>
        <img class="device-image" src="${imgOffLineToShow}" />
        <div class="device-data-row">
          <div class="consumption">⚡ <span>-- kW</span></div>
        </div>
      </div>
    `);

    const backColor = ds.dataKey?.settings?.backGroundColor;
    if (backColor) $card.css('background-color', backColor);

    ctx.entityMap[label] = {
      el: $card,
      label,
      consumption: null
    };

    ctx.groups[group].push({ ds, cardHtml: $card });
  });

  const $groupView = $('<div id="group-view" class="status-container"></div>');
  ctx.$container.append($groupView);

  Object.entries(ctx.groups).forEach(([groupName, items]) => {
    const $groupCard = $(`
      <div class="device-card-centered clickable" data-group="${groupName}">
        <div class="device-title">${groupName}</div>
        <img class="device-image" src="/api/images/public/group_icon.png" />
        <div class="device-data-row"><span>${items.length} dispositivos</span></div>
      </div>
    `);
    $groupView.append($groupCard);
  });

  ctx.$container.on('click', '[data-group]', function () {
    const groupName = $(this).data('group');
    const $detailView = $('<div id="detail-view" class="status-container"></div>');

    const $backBtn = $('<button class="back-button">⬅ Voltar</button>');
    $backBtn.on('click', function () {
      $('#detail-view').remove();
      $('#group-view').show();
    });

    $detailView.append($backBtn);

    ctx.groups[groupName].forEach(({ cardHtml }) => {
      $detailView.append(cardHtml);
    });

    $('#group-view').hide();
    ctx.$container.append($detailView);
  });

  ctx.$container.on('click', '.device-card-centered', function (e) {
    const entityId = $(this).data('entity-id');
    const entityType = $(this).data('entity-type');
    if (entityId && entityType) {
      e.preventDefault();
      ctx.stateController.openState('default', {
        entityId: { id: entityId, entityType }
      }, false);
    }
  });
};

self.onDataUpdated = function () {
  const ctx = self.ctx;

  ctx.data.forEach(ds => {
    const label = ds.datasource?.entityLabel;
    const key = ds.dataKey?.name;
    const val = ds.data?.[ds.data.length - 1]?.[1];
    const isValid = typeof val === 'number' && !isNaN(val);

    if (key === 'consumption' && ctx.entityMap[label]) {
      const entry = ctx.entityMap[label];
      const isOn = isValid && val > 0;

      let img;

      if (label.toLowerCase().includes("rel")) {
        img = !isValid ? storeOffline : (isOn ? relogioOn : relogioOff);
      } else if (label.toLowerCase().includes("bomba") || label.toLowerCase().includes("chiller")) {
        img = !isValid ? storeOffline : (isOn ? bombaOn : bombaOff);
      } else if (label.toLowerCase().includes("subesta")) {
        img = !isValid ? storeOffline : (isOn ? subestacaoOn : subestacaoOff);
      } else {
        img = !isValid ? storeOffline : (isOn ? storeOn : storeOff);
      }

      const consumo = isValid ? `${(val / 1000).toFixed(2)} kW` : '-- kW';
      const color = isOn ? '#28a745' : '#777';

      entry.el.find('.device-image').attr('src', img).toggleClass('blink', isOn);
      entry.el.find('.consumption span').text(consumo).css('color', color);
      entry.consumption = consumo;
    }
  });
};

