descriptor.templateCss:
#mySidenav{
    display: flex;
    flex-direction: row;
    justify-content: space-around;
    align-items: center;
    background-color: #0f0f17;
    width: 100%;
    height: 100%;
    border-radius: 10px;
}
nav{
    display: flex;
    flex-direction: row;
    justify-content: space-around;
    align-items: center;
    border-bottom: 1px solid rgba(64, 84, 178, .25);
    width: 100%;
}
#mySidenav a{
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
  transition: 0.3s;
  padding: 10px;
 
  text-decoration: none;
  font-size: 16px;
  color: white;
  background-color: #0f0f17;
  border-bottom: 1px solid rgba(64, 84, 178, 0);
}

#mySidenav a:hover {
  color: #0FC080;
  border-bottom: 1px solid #0FC080;
}
#mySidenav a:hover:not {
  border-bottom: 1px solid #0FC080;
  color: #0FC080;
}
#mySidenav a:focus{
    border-bottom: 1px solid #0FC080;
    color: #0FC080;
}
#mySidenav a:active{
    border-bottom: 1px solid #0FC080;
    color: #0FC080;
}

descriptor.controllerScript:
self.onInit = function() {
    self.ctx.$scope.links = self.ctx.settings.links ||
    [];

    self.ctx.$scope.groupDashboardId = self.ctx.settings.groupDashboardId
    const groupDashboardId = self.ctx.settings.groupDashboardId
    console.log("seting",self.ctx.settings)
    function changeDashboardState(e, stateId) {
        e.preventDefault();
            // self.ctx.stateController.openState(stateId, {},false)
            self.ctx.router.navigateByUrl('/dashboards/'+ groupDashboardId + '?state=' + stateId)

        //   self.ctx.router.navigate(self.ctx.stateController.openState)
    }

    self.ctx.$scope.changeDashboardState =
        changeDashboardState;
}

