descriptor.settingsSchema:
{
  "schema": {
    "type": "object",
    "title": "Settings",
    "properties": {
      "linkCount": {
        "title": "Número de Links",
        "type": "integer",
        "minimum": 1,
        "default": 1
      },
         "groupDashboardId":{
        "type": "string",
        "title": "Dashboard Dashboard ID",
        "default": "" 
      },
      "links": {
        "type": "array",
        "title": "Configuração dos Links",
        "items": {
          "type": "object",
          "properties": {
            "content": {
              "title": "Menu String",
              "type": "string",
              "default": "Menu Entry"
            },
            "stateId": {
              "title": "State ID",
              "type": "string",
              "default": ""
            },
            "enableLink": {
              "title": "Habilitar Link",
              "type": "boolean",
              "default": false
            }
          }
        },
        "minItems": 1,
        "maxItems": 10
      }
    }
  }
}
