descriptor.templateCss:
.status-container {
  display: flex;
  flex-direction: column;
  padding: 6px;
  height: 100%;
  overflow-y: auto;
  overflow-x: hidden;
  box-sizing: border-box;
}

.device-card-centered {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  border-radius: 12px;
  background: #fff;
  padding: 6px;
  box-shadow: 0px 10px 10px rgba(0,0,0,0.06), 0px -6px 6px rgba(0,0,0,0.04);
  text-align: center;
  width: 99%;
  max-width: 100%;
  margin: 0 2px 12px 2px;
  cursor: pointer;
  transition: box-shadow 0.2s ease, transform 0.1s ease;
}

.device-card-centered:hover {
  box-shadow: 0px 8px 24px rgba(0,0,0,0.12), 0px -4px 16px rgba(0,0,0,0.08);
  transform: scale(1.1);
}

.device-title {
  font-weight: bold;
  font-size: 0.9rem;
  margin-bottom: 6px;
  color: #333;
}

/* Alinha imagem e dados lado a lado */
.device-direction {
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: flex-start; /* em vez de space-between */
  width: 100%;
  gap: 8px; /* menor gap */
}

/* Tamanho fixo da imagem */
.device-image {
  width: 90px;
  height: 90px;
  object-fit: contain;
  transition: transform 0.2s ease;
}

/* Dados (temperatura e consumo) alinhados ao centro vertical da imagem */
.device-data-row {
  padding-left: 0; /* remover espa√ßamento */
  align-items: flex-start;
}

/* Temperatura */
.temperature {
  font-size: 0.9rem;
  margin: 2px 0;
  display: flex;
  align-items: center;
  gap: 4px;
}

.temperature span {
  color: #007bff;
  font-weight: 600;
}

/* Consumo */
.consumption {
  font-size: 0.9rem;
  margin: 2px 0;
  display: flex;
  align-items: center;
  gap: 4px;
}

.consumption span {
  color: #28a745;
  font-weight: 600;
}

/* Efeito blink (para motores ligados) */
.blink {
  animation: blink 1.2s infinite;
}

@keyframes blink {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.3; }
}

descriptor.controllerScript:
const DEBUG = true;
const MinConsumption = 5;

function log(...args) {
  if (DEBUG) console.log('[DEBUG]', ...args);
}

const noDevice = '/api/images/public/g7phsMSdCo51gWcoJgi3QrKUSwj9njtC';
const offlineImg = '/api/images/public/XVSlrbdXz5jAFfYNo4ymvu3jh76Iw6Ag';
const offImg = '/api/images/public/V3nAuG6sBlMJAAOeiXWhctFKZzBuo6IL';
const fanImg = '/api/images/public/4A8Sk4WP8QuPqyxwZXCF9I08HxQsbKBy';
const onImg = '/api/images/public/6ziChYbLxcZuCismHWEBvCWNj6LLUet0';
const arSelfContainedOffImg = '/api/images/public/G5ldxE6QEljmGxLyUGkjHQt3ddUtbPax';
const arSelfContainedFanImg = '/api/images/public/nAqgFLTCDHSyrCaboKq6R31Q45xI4NNT';
const arSelfContainedOnImg = '/api/images/public/Huwu3DqdnwB1N9mqlcSRsWzKUD3dPwtJ';

function getGrupoFromLabel(label) {
  if (!label || typeof label !== 'string') return null;
  const lower = label.toLowerCase();

  if (lower.includes('sala online')) return 'Sala Online';
  const arMatch = lower.match(/ar\s*(\d{1,2})/);
  if (arMatch) {
    const num = arMatch[1].padStart(2, '0');
    return `√Årea Comum ${num}`;
  }

  if (lower.includes('temperatura sala online')) return 'Sala Online';
  if (lower.includes('temperatura √°rea comum')) return '√Årea Comum';

  return null;
}

function createCard(grupo, entityId, entityType, state, imageUrl, backColor) {
  const safeId = `card-${grupo.replace(/\s+/g, '_')}`;
  const $card = $(`
    <div class="device-card-centered clickable"
         id="${safeId}"
         data-state="${state}"
         data-entity-id="${entityId || ''}"
         data-entity-type="${entityType || ''}">
      <div class="device-title">üìç ${grupo}</div>
      <div class="device-direction">
        <img class="device-image" src="${imageUrl}" />
        <div class="device-data-row">
          <div class="temperature">üå°Ô∏è <span>--¬∞C</span></div>
          <div class="consumption">‚ö° <span>-- kW</span></div>
        </div>
      </div>
    </div>
  `);
  if (backColor) $card.css('background-color', backColor);
  return $card;
}

function getImageByState(isOnline, isOn, isFan) {
  return isOn ? (isFan ? fanImg : onImg) : offImg;
}

function updateTemperatureInGroup(ctx, grupo, tempText) {
  const entries = ctx.grupoToCards[grupo] || [];
  entries.forEach(entry => {
    entry.el.find('.temperature span').text(tempText);
    entry.temperature = tempText;
  });
}

function updateCardConsumption(ctx, grupo, val) {
  const entries = ctx.grupoToCards[grupo] || [];
  const isOn = val > MinConsumption;
  const isFan = val > MinConsumption && val <= ctx.settings.threshold;
  const color = isOn ? (isFan ? '#b499eb' : '#45b2cc') : '#d6dcdd';
  const consumoText = typeof val === 'number' ? `${(val / 1000).toFixed(2)} kW` : '-- kW';
  const img = isOn ? (isFan ? arSelfContainedFanImg : arSelfContainedOnImg) : noDevice;

  entries.forEach(entry => {
    entry.el.find('.device-image').attr('src', img).toggleClass('blink', isOn);
    entry.el.find('.consumption span').text(consumoText).css('color', color);
    entry.consumption = consumoText;
  });
}

self.onInit = function () {
  const ctx = self.ctx;
  ctx.grupoToCards = {};
  ctx.temperaturas = {};
  ctx.$container.empty();

  const $main = $('<div class="status-container"></div>');
  ctx.$container.append($main);

  ctx.data.forEach(ds => {
    const label = ds.datasource?.entityLabel;
    const key = ds.dataKey?.name;
    const grupo = getGrupoFromLabel(label);

    if (!grupo) return;

    if (key === 'temperature') {
      ctx.temperaturas[grupo] = { label, valor: null };
    }

    if (key === 'consumption') {
      const entityId = ds.datasource?.entityId;
      const entityType = ds.datasource?.entityType;
      const backColor = ds.dataKey?.settings?.backGroundColor;
      const $card = createCard(grupo, entityId, entityType, '', noDevice, backColor);
      $main.append($card);

      ctx.grupoToCards[grupo] = ctx.grupoToCards[grupo] || [];
      ctx.grupoToCards[grupo].push({ el: $card, label });
    }
  });

  const ordem = ['√Årea Comum 01', '√Årea Comum 02', '√Årea Comum 03', 'Sala Online'];
  $main.children().sort((a, b) => {
    const ga = $(a).find('.device-title').text();
    const gb = $(b).find('.device-title').text();
    return ordem.indexOf(ga.trim()) - ordem.indexOf(gb.trim());
  }).appendTo($main);

  ctx.$container.on('click', '.device-card-centered', function (e) {
    e.preventDefault();
    const targetState = $(this).data('state');
    const entityId = $(this).data('entity-id');
    const entityType = $(this).data('entity-type');
    if (entityId && entityType && targetState) {
      ctx.stateController.openState(targetState, {
        entityId: { id: entityId, entityType }
      }, false);
    }
  });
};

self.onDataUpdated = function () {
  const ctx = self.ctx;

  ctx.data.forEach(ds => {
    const label = ds.datasource?.entityLabel;
    const key = ds.dataKey?.name;
    const val = ds.data?.[ds.data.length - 1]?.[1];
    const isValid = typeof val === 'number' && !isNaN(val);

    const grupo = getGrupoFromLabel(label);
    if (!grupo) return;

    if (key === 'temperature') {
      const tempText = isValid ? `${val.toFixed(2)}¬∞C` : '--¬∞C';
      ctx.temperaturas[grupo] = ctx.temperaturas[grupo] || {};
      ctx.temperaturas[grupo].valor = tempText;
      updateTemperatureInGroup(ctx, grupo, tempText);
    }

    if (key === 'consumption') {
      updateCardConsumption(ctx, grupo, val);
    }
  });

  // fallback: aplica temperatura salva se n√£o atualizada ainda
  Object.entries(ctx.temperaturas).forEach(([grupo, { valor }]) => {
    if (valor) {
      updateTemperatureInGroup(ctx, grupo, valor);
    }
  });
};

