descriptor.templateCss:
.status-container {
  display: flex;
  flex-direction: column;
  padding: 6px;
  height: 100%;
  overflow-y: auto;
  overflow-x: hidden;
  box-sizing: border-box;
}

.device-card-centered {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  border-radius: 12px;
  background: #fff;
  padding: 6px;
  box-shadow: 0px 10px 10px rgba(0,0,0,0.06), 0px -6px 6px rgba(0,0,0,0.04);
  text-align: center;
  width: 99%;
  max-width: 100%;
  margin: 0 2px 12px 2px;
  cursor: pointer;
  transition: box-shadow 0.2s ease, transform 0.1s ease;
}

.device-card-centered:hover {
  box-shadow: 0px 8px 24px rgba(0,0,0,0.12), 0px -4px 16px rgba(0,0,0,0.08);
  transform: scale(1.1);
}

.device-title {
  font-weight: bold;
  font-size: 0.9rem;
  margin-bottom: 6px;
  color: #333;
}

/* Alinha imagem e dados lado a lado */
.device-direction {
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: flex-start; /* em vez de space-between */
  width: 100%;
  gap: 8px; /* menor gap */
}

/* Tamanho fixo da imagem */
.device-image {
  width: 90px;
  height: 90px;
  object-fit: contain;
  transition: transform 0.2s ease;
}

/* Dados (temperatura e consumo) alinhados ao centro vertical da imagem */
.device-data-row {
  padding-left: 0; /* remover espa√ßamento */
  align-items: flex-start;
}

/* Temperatura */
.temperature {
  font-size: 0.9rem;
  margin: 2px 0;
  display: flex;
  align-items: center;
  gap: 4px;
}

.temperature span {
  color: #007bff;
  font-weight: 600;
}

/* Consumo */
.consumption {
  font-size: 0.9rem;
  margin: 2px 0;
  display: flex;
  align-items: center;
  gap: 4px;
}

.consumption span {
  color: #28a745;
  font-weight: 600;
}

/* Efeito blink (para motores ligados) */
.blink {
  animation: blink 1.2s infinite;
}

@keyframes blink {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.3; }
}

descriptor.controllerScript:
const DEBUG = true;
const MinConsumption = 5;

function log(...args) {
  if (DEBUG) console.log('[DEBUG]', ...args);
}

// Imagens
const noDevice = '/api/images/public/g7phsMSdCo51gWcoJgi3QrKUSwj9njtC';
const offlineImg = '/api/images/public/XVSlrbdXz5jAFfYNo4ymvu3jh76Iw6Ag';
const offImg = '/api/images/public/V3nAuG6sBlMJAAOeiXWhctFKZzBuo6IL';
const fanImg = '/api/images/public/4A8Sk4WP8QuPqyxwZXCF9I08HxQsbKBy';
const onImg = '/api/images/public/6ziChYbLxcZuCismHWEBvCWNj6LLUet0';
const arSelfContainedOffImg = '/api/images/public/G5ldxE6QEljmGxLyUGkjHQt3ddUtbPax';
const arSelfContainedFanImg = '/api/images/public/nAqgFLTCDHSyrCaboKq6R31Q45xI4NNT';
const arSelfContainedOnImg = '/api/images/public/Huwu3DqdnwB1N9mqlcSRsWzKUD3dPwtJ';

function isAr(label) {
  return /ar/i.test(label) && !/temperatura|termostato/i.test(label);
}
function isTemperatura(label) {
  return /temperatura|termostato/i.test(label);
}

function getGrupoFromLabel(label) {
  if (/sala online/i.test(label)) return 'Sala Online';
  if (/ar 01/i.test(label)) return '√Årea Comum 01';
  if (/ar 02/i.test(label)) return '√Årea Comum 02';
  return null;
}

function createCard(grupo, temp, consumo, imageUrl, blink, color) {
  const safeId = `card-${grupo.replace(/\s+/g, '_')}`;
  const $card = $(`
    <div class="device-card-centered clickable" id="${safeId}">
      <div class="device-title">üìç ${grupo}</div>
      <div class="device-direction">
        <img class="device-image ${blink ? 'blink' : ''}" src="${imageUrl}" />
        <div class="device-data-row">
          <div class="temperature">üå°Ô∏è <span>${temp}</span></div>
          <div class="consumption">‚ö° <span style="color:${color}">${consumo}</span></div>
        </div>
      </div>
    </div>
  `);
  return $card;
}

self.onInit = function () {
  const ctx = self.ctx;
  ctx.$container.empty();
  const $main = $('<div class="status-container"></div>');
  ctx.$container.append($main);

  const grupos = {};

  ctx.data.forEach(ds => {
    const label = ds.datasource?.entityLabel;
    const key = ds.dataKey?.name;
    const val = ds.data?.at(-1)?.[1];
    const grupo = getGrupoFromLabel(label);
    if (!grupo) return;

    if (!grupos[grupo]) grupos[grupo] = { ar: null, temperatura: null };

    if (isAr(label) && key === 'consumption') {
      grupos[grupo].ar = { label, val };
    }
    if (isTemperatura(label) && key === 'temperature') {
      grupos[grupo].temperatura = { label, val };
    }
  });

  log("Grupos detectados:", grupos);

  Object.entries(grupos).forEach(([grupo, dados]) => {
    const consumo = dados.ar?.val ?? null;
    const temp = dados.temperatura?.val ?? null;
    const tempText = typeof temp === 'number' ? `${temp.toFixed(2)}¬∞C` : '--¬∞C';
    const consumoText = typeof consumo === 'number' ? `${(consumo / 1000).toFixed(2)} kW` : '-- kW';

    const isOn = typeof consumo === 'number' && consumo > MinConsumption;
    const isFan = consumo > MinConsumption && consumo <= (ctx.settings?.threshold || 100);
    const img = dados.ar ? (isFan ? arSelfContainedFanImg : arSelfContainedOnImg) : noDevice;
    const cor = isOn ? (isFan ? '#b499eb' : '#45b2cc') : '#d6dcdd';

    const $card = createCard(grupo, tempText, consumoText, img, isOn, cor);
    $main.append($card);
  });
};

self.onDataUpdated = self.onInit;

